import * as React from 'react';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import axios from 'axios';

import { useForm } from "react-hook-form";




export default function SimplePaper() {
  const [hasError, setHasError] = React.useState(false)
  const [title, setTitle] = React.useState([]);
  const [description, setDescription] = React.useState([]);
  console.log(React.state);

  React.useEffect(async () => {
    await axios.post("http://localhost:8080/api", {
      headers: {
        "Content-Type": `application/json`,
      }}
      ).then(e => JSON.stringify(e.target[0].value)).catch(err => setHasError(true)).then(res => console.log(res))}, []);
      const handleSubmit = (e) => {
        e.preventDefault();
        alert(JSON.stringify(e.target[0].value));
      };
      const handleChange = ({ target: { value } }) => setTitle(value);
      const handleChangeDes = ({ target: { value } }) => setDescription(value);
      
      return (
    <form onSubmit={handleSubmit}>
      <Paper elevation={3} 
        sx={{
          display: 'flex',
          flexBasise: 0,
          flexDirection: 'row',
          justifyContent: 'flex-start',
          alignItems: 'center',
          hight: '100vh'

        }}
      >
        <Box sx={{
          m: 2, 
          display: 'flex',
          flexDirection: 'column',
          justifyContent:'flex-start',
          flexGrow: 3
          }}>
          <TextField id="outlined-basic" label="Outlined" variant="outlined" margin="normal" sx={{width: '100%'}} >
            <input type="text" name="title" value={title} onChange={handleChange}/>
          </TextField>
          <TextField
            id="outlined-multiline-static"
            name="description"
            label="Multiline"
            multiline
            rows={20}
            margin="normal"
            // value={value}
            // onChange={handleChange}
            sx={{width: '100%'}}
          >
            <textarea name="description" value={description} onChange={handleChange}/>
          </TextField>
          <Box sx={{
            display: 'flex',
            justifyContent:'center'
            }}>
            <Button type="submit" variant="contained">확인</Button>
            <Button variant="contained">취소</Button>
          </Box>
        </Box>
      </Paper>
    </form>
  );
}
